document.querySelectorAll("[style]").forEach(function(e){e.attributes.style.value.split(";").forEach(function(t){0===t.indexOf("background-color:")&&e.style.setProperty("--bg",t.split(":")[1]),0===t.indexOf("color:")&&e.style.setProperty("--cl",t.split(":")[1])}),Object.keys(window.griddPalette).forEach(function(t){e.classList.contains("has-"+t+"-color")&&e.style.setProperty("--cl",window.griddPalette[t]),e.classList.contains("has-"+t+"-background-color")&&e.style.setProperty("--bg",window.griddPalette[t])})});