document.querySelectorAll("[style]").forEach(function(t){t.attributes.style.value.split(";").forEach(function(e){0===e.indexOf("background-color:")&&t.style.setProperty("--bg",e.split(":")[1]),0===e.indexOf("color:")&&t.style.setProperty("--cl",e.split(":")[1])})}),Object.keys(window.griddPalette).forEach(function(t){document.querySelectorAll(".has-"+t+"-color").forEach(function(e){e.style.setProperty("--cl",window.griddPalette[t])}),document.querySelectorAll(".has-"+t+"-background-color").forEach(function(e){e.style.setProperty("--bg",window.griddPalette[t])})});